jQuery(document).ready(function(jQuery){var meta_image_frame;jQuery('#katracker_thumb_button').click(function(e){e.preventDefault();if(meta_image_frame){meta_image_frame.open();return;}
meta_image_frame=wp.media.frames.meta_image_frame=wp.media({title:meta_image.title,button:{text:meta_image.button},library:{type:'image'}});meta_image_frame.on('select',function(){var media_attachment=meta_image_frame.state().get('selection').first().toJSON();jQuery('#katracker_thumb').empty();jQuery('#katracker_thumb').val(media_attachment.id);jQuery('#katracker_thumb_button span').hide();jQuery('#katracker_edit_thumb').css('display','inline-block');jQuery('#katracker_edit_thumb').attr('href',"http://www.kateam.org/wp-admin/post.php?post="+media_attachment.id+"&action=edit&image-editor&TB_iframe=1");jQuery('#katracker_remove_thumb').css('display','inline-block');jQuery('.wp-post-image').attr('src',media_attachment.url);jQuery('.wp-post-image').css('display','block');});meta_image_frame.open();});jQuery('#katracker_remove_thumb').click(function(e){jQuery('#katracker_thumb').removeAttr('value');jQuery('.wp-post-image').hide();jQuery('#katracker_thumb_button span').show();jQuery('#katracker_remove_thumb').hide();jQuery('#katracker_edit_thumb').hide();});});