//magazine page 
jQuery(document).ready(function($) {
        var sh = true;
	      if (navigator.userAgent.match(/chrome/i) ){
	      	sh = false;
	      };
	      var magauto = $('.magazine-wrap #bb-bookblock').data('autoplay');
	      var aut = magauto;
	      
	      var maginterval = $('.magazine-wrap #bb-bookblock').data('timeout');
	      var tim = maginterval;

	      var direction = $('.magazine-wrap #bb-bookblock').data('direction');

		var Page = (function() {
				
				var config = {
						$bookBlock : $( '.magazine-wrap #bb-bookblock' ),
						$navNext : $( '.magazine-wrap #bb-nav-next' ),
						$navPrev : $( '.magazine-wrap #bb-nav-prev' )
					},
					
      					init = function() {
						
						config.$bookBlock.bookblock( {
							speed : 1000,
							shadowSides : 0.8,
							shadowFlip : 0.4,
							autoplay : aut,
							interval : tim,
							shadows     : sh,
							direction : direction
						} );
						initEvents();
					},
					initEvents = function() {
						
						var $slides = config.$bookBlock.children();

						// add navigation events
						config.$navNext.on( 'click touchstart', function() {
							config.$bookBlock.bookblock( 'next' );
							return false;
						} );

						config.$navPrev.on( 'click touchstart', function() {
							config.$bookBlock.bookblock( 'prev' );
							return false;
						} );
						
						// add swipe events
						$slides.on( {
							'swipeleft' : function( event ) {
								config.$bookBlock.bookblock( 'next' );
								return false;
							},
							'swiperight' : function( event ) {
								config.$bookBlock.bookblock( 'prev' );
								return false;
							}
						} );

						// add keyboard events
						$( document ).keydown( function(e) {
							var keyCode = e.keyCode || e.which,
								arrow = {
									left : 37,
									up : 38,
									right : 39,
									down : 40
								};

							switch (keyCode) {
								case arrow.left:
									config.$bookBlock.bookblock( 'prev' );
									break;
								case arrow.right:
									config.$bookBlock.bookblock( 'next' );
									break;
							}
						} );
					};

					return { init : init };

			})();
                   	Page.init();
                    });